import React, { useRef, useState, useEffect } from "react";
import { Link, NavLink, useHistory } from "react-router-dom";
import firebase from "../firebase";

import "../assets/sty_login.css";
import { FirebaseError } from "@firebase/util";

function Login() {
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [hasAccount, setHasAccount] = useState(false);

  const history = useHistory();

  function handleClick() {
    Firebase.auth()
      .createUserWithEmailAndPasswordSuccess(emailAddress, password)
      .then((result) => {
        history.push("/");
      });
  }

  return (
    <div>
      <div className="backgroundWrapper">
        <div className="loginWapper">
          <h1>LOGIN</h1>
          <form action="" className="inputGroup">
            <div className="loginForm">
              <input type="text" className="emailAddress" required />
              <label for="emailAddress">Email Address</label>
            </div>
          </form>
          <form action="" className="inputGroup">
            <div className="passw">
              <input type="password" className="password" required />
              <label for="password">Password</label>
            </div>
          </form>
          <div className="btnGroup">
            <div className="login_Btn">
              <button type="submit" onClick={handleClick}>
                LOG-IN
              </button>
            </div>
            <div className="joinUs">
              <h3>You do not have a account?</h3>
              <NavLink to="/Joinus" className="joinlink">
                JOIN-US
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
