import React, { useEffect } from "react";
import useStorage from "../hook/useStorage";

const ProgressBar = ({ file, setFile }) => {
  const { url, progress } = useStorage(file);

  useEffect(() => {
    if (url) {
      setFile(null);
    }
  }, [url, setFile]);

  return (
    <div
      className="progressbar"
      style={{
        width: progress + "%",
      }}
    >
      Completed!
    </div>
  );
};

export default ProgressBar;
