import React, { useState } from "react";
import "../assets/image_eu/berlin_germany.jpg";
import data from "./img_data";
import Img1 from "../assets/image_eu/berlin_germany.jpg";

const PhotoEu = () => {
  const [modal, setModal] = useState(false);
  const [tempdata, setTempData] = useState([]);

  const getData = (img, title, desc) => {
    let tempData = [img, title, desc];
    setTempData((item) => [1, ...tempData]);
    return setModal(true);
  };
  return (
    <>
      <section className="py-4 py-lg-5 container">
        <div className="row justify-content-center align-item-center">
          {data.cardData.map((e, index) => {
            return (
              <div className="col-11 col-md-6 col-lg-3 mx-0 mb-4" key={index}>
                <div className="card p-0 overflow-hidden h-100 shadow">
                  <img src={e.imgSrc} className="card-img-top" />
                  <div className="card-body">
                    <h5 className="card-title">{e.title}</h5>
                    <p className="card-text">{e.desc}</p>
                    <button
                      className="btn btn-primary"
                      onClick={() => getData(e.imgSrc, e.title, e.desc)}
                    >
                      More Information
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </section>
    </>
  );
};

export default PhotoEu;
