import React, { useState } from "react";
import "../assets/image_eu/berlin_germany.jpg";
import data from "./eu_imgData";

const Eucard = () => {
  const [modal, setModal] = useState(false);
  const [tempdata, setTempData] = useState([]);

  const getData = (img, title, desc) => {
    let tempData = [img, title, desc];
    setTempData((item) => [1, ...tempData]);
    return setModal(true);
  };
  return (
    <>
      <section className="mainContain">
        {data.cardData.map((e, index) => {
          return (
            <div className="card" key={index}>
              <div className="cardImg">
                <img src={e.imgSrc} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{e.title}</h5>
                  <p className="card-text">{e.desc}</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => setbuttonPopup(true)}
                  >
                    More Information
                  </button>
                </div>
              </div>
            </div>
          );
        })}
      </section>
    </>
  );
};

export default Eucard;
