import React, { useState } from "react";
import ButtonAppBar from "../component/navbar";
import "../assets/sty_gallery.css";
function Gallery() {
  //html,css 페이지 아웃라인

  //사진 내컴퓨터에서 불러오기 후 데이터 저장

  //데이터 가져오기, 화면에 출력
  // const db = firebase.firestore();
  // db.collection('product').get().then(snapshot)=>{
  //     snapshot.forEach((doc)=>{
  //         console.log(doc.data())
  //     })
  // }
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);

  const types = ["image/png", "image/jpeg", "image/jpg"];

  const changeHandelr = (e) => {
    let selected = e.target.files[0];

    if (selected && types.includes(selected.types)) {
      setFile(selected);
    } else {
      setFile(null);
      setError("Please select an image file (png, jpeg or jps) ");
    }
  };
  // 22분 40초 output div 만들고...

  return (
    <div>
      <div className="navbar">
        <ButtonAppBar />
      </div>
      <div className="sectionTitle">
        <h1>MY GALLERY</h1>
        <form className="uploadform">
          <input type="file" onChange={changeHandelr} />
          <div className="output">
            {error & <div className="error">{setError}</div>}
          </div>
        </form>
      </div>
    </div>
  );
}

export default Gallery;
